edges.reduce:
function(keys, values, rereduce) {
  var results = [];
  if (!rereduce) {
    values.forEach(function(value) {
      if (results.indexOf(value) === -1) {
        results.push(value);
      }
    });
  } else {
    values.forEach(function(arr) {
      arr.forEach(function(value) {
        if (results.indexOf(value) === -1) {
          results.push(value);
        }
      });
    });
  }
  return results;
}

