fts.index:
function(doc) {
    var fieldsToIndex = ["fname", "content", "user", "name", "group", "channel", "dataset", "dtype", "orgType"];
    if(doc.hasOwnProperty("sources")){
      for (var x = 0; x < doc.sources.length; x++){
        index("sources", doc.sources[x], {"store": true})
      }
    }
    for (var i = 0; i < fieldsToIndex.length; i++) {
        var field = fieldsToIndex[i];
        if (doc.hasOwnProperty(field)) {
            var value = doc[field];
            if (typeof value !== 'undefined') {
                index(field, value, {"store": true});
            }
        }
    }
}
